class tdhConsultaTipo extends BasetdhConsultaTipo
{
  public function __toString()
  {
    return $this->getNombre();
  }
  
  /**
   * Comprueba si un usuario dado estÃ¡ autorizado a realizar respuestas y gestiÃ³n a una consulta.
   * 
   * @param tdhSecurityUser $user
   * @return boolean
   */
  public function esUsuarioAutorizado(tdhSecurityUser $user)
  {
    if($user->isAuthenticated())
    {
      if($user->isAdministrador())
      {
        return true;
      }
      
      foreach($this->getConsultores() as $consultor)
      {
        if($consultor->getUsername() == $user->getUserName())
        {
          return true;
        }
      }
    }
    
    return false;
  }
}